SELECT applyId , uid, borrowApplyId , applyName , telephone , applyAmount , cityName , cityArea , haveDetail , pageReferer , channelCode , channelDetail , applyType , identifyNo , education , workType , isRepeat, cmpType , creditType , pubAmount , caseAmount , totalAmount , manageYear , wagesType , income , iszhapian, fundType , socialType , houseType , carType , haveWeiLi , zimaScore , insurType , insurMonth , insurMonthAmt , DATE_FORMAT(applyTime,'%Y-%m-%d %H:%i:%s') as "applyTime", DATE_FORMAT(updateTime,'%Y-%m-%d %H:%i:%s') as "updateTime", DATE_FORMAT(tranferTime,'%Y-%m-%d %H:%i:%s') as "tranferTime", DATE_FORMAT(tranTime,'%Y-%m-%d %H:%i:%s') as "tranTime", isAgain , status , age , sex , carMonthPay , houseMonthPay , insure , loanDeadline , insurPayType , fundMonth , socialMonth , houseVal , carPrice , hasLicense , applyIp , jobMonth , housePlace , carLocal , carYears , myLicense , myLoanHouse , houseMonth , carMonth , pageDetail , errMsg , DATE_FORMAT(birthday,'%Y-%m-%d'), userAgent FROM t_apply
ORDER by ${orderSql}
limit 0,#{limitSize}

select applyId, uid, status,(SELECT count(applyId) from t_apply where telephone = #{telephone}) as count, applyTime 
from t_apply where telephone = #{telephone} 
ORDER BY applyId DESC LIMIT 0,1

SELECT IF ( workType IS NULL and houseType is null AND carType is null, 0, 1 ) AS 'isLoan' FROM t_apply
and uid = #{uid}
and applyId = #{applyId}
