mapper.select:
SELECT t.pushId, t1.applyId , t1.applyName , t1.telephone , t1.applyAmount , t1.cityName , t1.cityArea , t1.channelCode , t1.channelDetail , t1.identifyNo , t1.education , t1.workType , t1.cmpType , t1.creditType , t1.pubAmount , t1.caseAmount , t1.totalAmount , t1.manageYear , t1.wagesType , t1.income , t1.iszhapian, t1.fundType , t1.socialType , t1.houseType , t1.carType , t1.haveWeiLi , t1.zimaScore , t1.insurType , t1.insurMonth , t1.insurMonthAmt , DATE_FORMAT(t1.applyTime,'%Y-%m-%d %H:%i:%s') as "applyTime", t1.age , t1.sex , t1.carMonthPay , t1.houseMonthPay , t1.insure , t1.loanDeadline , t1.insurPayType , t1.fundMonth , t1.socialMonth , t1.houseVal , t1.carPrice , t1.hasLicense , t1.applyIp , t1.jobMonth , t1.housePlace , t1.carLocal , t1.carYears , t1.myLicense , t1.myLoanHouse , t1.houseMonth , t1.carMonth , DATE_FORMAT(birthday,'%Y-%m-%d') as 'birthday' FROM t_apply_push_record t JOIN t_apply t1 ON t.applyId = t1.applyId
and t.pushType = #{pushType}
and t.status = #{status}
and t.status in(${inStatus})
and t1.isRepeat=#{isRepeat}
and (t1.haveDetail=1 OR t1.applyTime<date_add(now(), interval #{limitMin} minute))
ORDER BY t1.applyTime DESC
limit 0,#{limitSize}
